const CRC32_DATA = CRC32_TABLE.split(" ").map(function (s) {
  return parseInt(s, 16);
});

export function crc32(bytes) {
  var crc = -1;
  for (var i = 0, iTop = bytes.length; i < iTop; i++) {
    crc = (crc >>> 8) ^ CRC32_DATA[(crc ^ bytes[i]) & 0xff];
  }
  return (crc ^ -1) >>> 0;
}

